import React, { useState, useEffect } from "react";
import {
  Container,
  AppBar,
  Typography,
  Grow,
  Grid,
  Button,
  MenuItem,
  Select,
  TextField,
  FormControl, FormControlLabel
} from "@material-ui/core";
import { useDispatch } from "react-redux";

import Posts from "../Posts/Posts";

import { getPosts } from "../../actions/posts";
import ResponsiveAppBar from "../NavigationBar/ResponsiveAppBar";

const Home = () => {
  const [currentId, setCurrentId] = useState(0);
  const [query, setQuery] = useState("");
  const [sortBy, setSortBy] = useState("None");

  const dispatch = useDispatch();

  const updateQuery = (e) => {
    setQuery(e.target.value);
  };

  const updateSortBy = (e) => {
    console.log(e.target.value);
    setSortBy(e.target.value);
  };

  useEffect(() => {
    dispatch(getPosts());
  }, [currentId, dispatch]);

  return (
    <Container maxWidth="lg">
      <ResponsiveAppBar />
      <TextField
        style={{
          marginTop: "150px",
          backgroundColor: "White",
          position: "relative",
          width: "50%",
          left: "25%",
        }}
        id="outlined-basic"
        label="Search for petitions!"
        variant="outlined"
        onChange={updateQuery}
      />
      <br />

      <Select
        style={{
          marginTop: "15px",
          backgroundColor: "White",
          position: "relative",
          width: "50%",
          left: "25%",
        }}
        inputProps={{ "aria-label": "Without label" }}
        onChange={updateSortBy}
      >
        <MenuItem value="Filter By: ">
          <em>None</em>
        </MenuItem>
        <MenuItem value={"like"}>Like Count</MenuItem>
        <MenuItem value={"creationTimeDesc"}>
          Creation Time (Newest to Oldest)
        </MenuItem>
        <MenuItem value={"creationTimeAsc"}>
          Creation Time (Oldest to Newest)
        </MenuItem>
      </Select>

      <Grow in>
        <Container style={{ marginTop: "20px" }}>
          <Grid
            container
            justify="space-between"
            alignItems="stretch"
            spacing={2}
          >
            <Grid item xs={12} sm={12}>
              <Posts setCurrentId={setCurrentId} query={query} setWidth={4} />
            </Grid>
          </Grid>
        </Container>
      </Grow>
    </Container>
  );
};

export default Home;
