import axios from 'axios';

const url = 'http://localhost:5000/petitions';

export const fetchPetitions = () => axios.get(url);
export const createPetition = (newPetition) => axios.post(url, newPost);
export const likePetition = (id) => axios.patch(`${url}/${id}/likePost`);
export const updatePetition = (id, updatedPost) => axios.patch(`${url}/${id}`, updatedPost);
export const deletePetition = (id) => axios.delete(`${url}/${id}`);
