    } else if (sortBy === "likeCountAsc") {
      petitions.sort((a, b) => (a.voteCount < b.voteCount ? -1 : 1));
    } else if (sortBy === "creationTimeDesc") {
      petitions.sort((a, b) => (a.creationTime < b.creationTime ? 1 : -1));
    } else if (sortBy === "creationTimeAsc") {
      petitions.sort((a, b) => (a.creationTime < b.creationTime ? -1 : 1));
