import * as React from "react";
import {
  AppBar,
  IconButton,
  Typography,
  Menu,
  Avatar,
  Button,
  Tooltip,
  MenuItem,
} from "@material-ui/core";
import AddCircleOutlineOutlinedIcon from "@material-ui/icons/AddCircleOutlineOutlined";
import { useHistory } from "react-router-dom";
import { useEffect } from "react";

import "./ResponsiveAppBar.css";

const ResponsiveAppBar = () => {
  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElUser, setAnchorElUser] = React.useState(null);
  const [isLoggedIn, setIsLoggedIn] = React.useState(false);

  async function verifyAndLoggingIn() {
    try {
      const res = await fetch("http://localhost:5000/isUserAuth", {
        headers: {
          "x-access-token": localStorage.getItem("token"),
        },
      });

      const data = await res.json();
      isLoggedIn = data.isLoggedIn;
    } catch (err) {
      console.log("Error in verifying user");
    }
  }

  const history = useHistory();
  const routeChangetoHome = () => {
    history.push("/");
  };

  const routeChangeToCreate = () => {
    history.push("/add");
  };

  const routeChangetoMyHome = () => {
    history.push("/myhome");
  };

  const routeChangetoLogin = () => {
    history.push("/signin");
  };

  const routeChangeToAbout = () => {
    history.push("/about");
  };

  const routeChangeToSignIn = () => {
    localStorage.removeItem("token");
    history.push("/signin");
  };

  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  useEffect(() => {verifyAndLoggingIn;}, []);

  return (
    <div>
      <AppBar position="fixed">
        <div className="nav_bar">
          <div className="nav_left">
            <div className="nav_title">
              <Typography variant="h5">PetitionRate</Typography>
            </div>
            <Button
              variant="contained"
              color="primary"
              className="nav_btn"
              onClick={routeChangeToAbout}
            >
              About
            </Button>
            <Button
              variant="contained"
              color="primary"
              className="nav_btn"
              onClick={routeChangetoHome}
            >
              Home
            </Button>
          </div>

          <div className="nav_profile">
            <Tooltip title="Create Petition">
              <Button size="big" onClick={routeChangeToCreate}>
                <AddCircleOutlineOutlinedIcon
                  fontSize="large"
                  style={{ fill: "yellow" }}
                />
              </Button>
            </Tooltip>
            <Tooltip title="Open Profile">
              <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                <Avatar src="/broken-image.jpg" />
              </IconButton>
            </Tooltip>
          </div>
          <Menu
            sx={{ mt: "45px" }}
            id="menu-appbar"
            anchorEl={anchorElUser}
            anchorOrigin={{
              vertical: "top",
              horizontal: "right",
            }}
            keepMounted
            transformOrigin={{
              vertical: "top",
              horizontal: "right",
            }}
            open={Boolean(anchorElUser)}
            onClose={handleCloseUserMenu}
          >
            <MenuItem key="My Home" onClick={routeChangetoMyHome}>
              <Typography textAlign="center">My Home</Typography>
            </MenuItem>
            <MenuItem key="Log In" onClick={routeChangetoLogin}>
              <Typography textAlign="center">Log In</Typography>
            </MenuItem>
            <MenuItem key="Sign Out" onClick={routeChangeToSignIn}>
              <Typography textAlign="center">Sign Out</Typography>
            </MenuItem>
          </Menu>
        </div>
      </AppBar>
    </div>
  );
};
export default ResponsiveAppBar;
