import React, { useState, useEffect } from "react";
import ResponsiveAppBar from "./components/NavigationBar/ResponsiveAppBar";


import axios from "axios";
import "./Description.css";
const Description = (props) => {
  let createdDate = "2018-06-12T19:30";
  const { id } = props.match.params;
  const [createDate, setCreateDate] = useState(null);
  const [voteCount, setVoteCount] = useState(0);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [userName, setUserName] = useState("");

  useEffect(() => {
    axios
      .get(`http://localhost:5000/petitions/${id}`)
      .then((result) => {
        setTitle(result.data.title);
        setDescription(result.data.description);
        setUserName(result.data.creator);
        setVoteCount(result.data.voteCount);
        const date = new Date(result.data.createdAt);
        setCreateDate(date.toString().split(" ").slice(0,5).join(" "));
      });
  }, []);

  return (
    <div>
      <ResponsiveAppBar />
      <div className="petition_container">
        <div className="petition_title">{title}</div>

        <div className="petition_description">{description}</div>
        <div
          className="petition_create_date"
          style={{ marginBottom: "2rem" }}
        >
          <span className="display_button">Author:</span>
          <input
            type="text"
            value={userName}
            disabled
            style={{ fontSize: "1.2rem", textAlign: "center", width: "300px", display: "inline-block" }}
          ></input>
        </div>
        <div
          className="petition_create_date"
          style={{ marginBottom: "2rem" }}
        >
          <span className="display_button">Create Date:</span>
          <input
            type="text"
            value={createDate}
            disabled
            style={{ fontSize: "1.2rem", textAlign: "center", width: "300px", display: "inline-block" }}
          ></input>
        </div>

        <div className="petition_Votecount">
          <span className="display_button">Vote Count:</span>
          <input
            type="number"
            value={voteCount}
            disabled
            style={{ fontSize: "1.2rem", textAlign: "center", width: "300px", display: "inline-block" }}
          ></input>
        </div>
      </div>
    </div>
  );
};

export default Description;
