import React, { useState, useEffect } from "react";
import ResponsiveAppBar from "./components/NavigationBar/ResponsiveAppBar";
import { Avatar } from "@material-ui/core";
import TextField from "@material-ui/core/TextField";

import axios from "axios";
import "./Description.css";
const Description = (props) => {
  let createdDate = "2018-06-12T19:30";
  const { id } = props.match.params;
  const [createDate, setCreateDate] = useState(null);
  const [voteCount, setVoteCount] = useState(0);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [userName, setUserName] = useState("");

  useEffect(() => {
    axios.get(`http://localhost:5000/petitions/${id}`).then((result) => {
      setTitle(result.data.title);
      setDescription(result.data.description);
      setUserName(result.data.creator);
      setVoteCount(result.data.voteCount);
      const date = new Date(result.data.createdAt);
      setCreateDate(date.toString());
    });
  }, []);

  return (
    <div>
      <ResponsiveAppBar />
      <div className="petition_container">
        <div className="petition_title">Petiton Title 1</div>

        <div className="UserContainer">
          <Avatar src="/broken-image.jpg" className="profile_pic" />
          <div className="username">UserName</div>
        </div>
        <div className="petition_description">
          Lorem ipsum dolor sit amet, eam tale doctus iracundia id. In est
          altera lobortis explicari. Sale tamquam invidunt sed ad. Aliquid
          ullamcorper delicatissimi ne cum, eu cum justo scripta interesset, sea
          elit nonumy aperiam et. Ut rebum doctus deleniti usu, ei vis omnium
          integre maiorum. At sumo intellegam mea. Assum sanctus fierent ad eos,
          ei minimum consequat eum. Eum agam honestatis et. Ne option prodesset
          quo, duo ex vidit complectitur. Pro epicuri appetere ne. Et modo sonet
          feugait vel. Et timeam utroque sed, voluptatum conclusionemque sit ad.
          Has tota iudico deterruisset at.
        </div>
        <div className="petition_create_date">Create Date:</div>
        <div className="petition_Deadline">Deadline:</div>
        <div className="petition_Votecount">Vote Count:</div>
        <div className="petition_Status">Status:</div>
      </div>
    </div>
  );
};

export default Description;
